tinymce.PluginManager.add("dotCustomButtons", function (editor) {

    editor.addButton("dotAddImage", {
        icon: "image",
        tooltip: editor.settings.dotLanguageStrings["insertImageLabel"],
        onclick: function (event) {

            parent = event.target;

            while (parent.className !== 'wysiwyg-container'){
                parent = parent.parentElement;
            }

            cmsFileBrowserImage.selectFolder = parent.dataset.selectFolder
                                                    .split(",")
                                                    .map(function (path) {return path.trim()});
            cmsFileBrowserImage.show();
        },
    });
});
