tinymce.PluginManager.add('dotimageclipboard', function(editor, url) {
    // Add a button that opens a window
    editor.addButton('dotimageclipboard', {
        image : url + '/img/image-clipboard.png',
        icon: false,
        tooltip:"Clipboard",
        onclick: function() {
            // Open window
			editor.windowManager.open({
				title: "dotCMS Image Clipboard",
				url: url + '/dialog.jsp',
				width: 550,
				height: 460
			});		         
        }
    });
    // Adds a menu item to the tools menu
    editor.addMenuItem('dotimageclipboard', {
        text: 'dotimageclipboard plugin',
        context: 'tools',
		onclick: function() {
            // Open window
			editor.windowManager.open({
				title: "dotCMS Image Clipboard",
				url: url + '/dialog.jsp',
				width: 550,
				height: 460
			});           
        }
    });
});