insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('COMPANY-COMMUNITY', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Company Community', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV>Liferay\'s community is a conglomeration of people worldwide. We owe our thanks to many people who have not only contributed to Liferay\'s code, but also to those that support us in many other ways which ultimately help us grow. <BR><BR>To&nbsp;join in our community: \n<TABLE cellSpacing=5 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD><IMG src=\"http://@portal_url@/image/image_gallery?img_id=15\"></TD>\n<TD class=alpha>Visit <A href=\"http://forums.liferay.com/\">http://forums.liferay.com</A></TD></TR>\n<TR>\n<TD><IMG src=\"http://@portal_url@/image/image_gallery?img_id=15\"></TD>\n<TD class=alpha><A href=\"http://lists.sourceforge.net/lists/listinfo/lportal-development\">Subscribe</A> or <A href=\"http://sourceforge.net/mailarchive/forum.php?forum=lportal-development\">browse</A>&nbsp;our mailing list</TD></TR></TBODY></TABLE><BR><B>Special thanks go to:</B> <BR><BR>Alysa Carver, Andrew Chen, Andrew Lin, Brett Randall, Caris Chan, Chohshin Koh, David Mendoza, David Moon, Dimitrios Bougoulias, Edwin Chung, Eric Nathanson, Florent Benoit, Fuikoon Lee, Gerry Schenk, Gilbert Jorgensen, Harry Mark, James Lefeu, James McGovern, Jeffrey Lau, Jesus Perez, John Hays, Jon Steer, Jorge Ferrer, Keith Davis, Kevin Verde, Lofi Dewanto, Marco Tedone, Maria Ahues, Mete Kural, Michael Weisser, Mirco Tamburini, Momchil Lalov, Nathan Peo, Nathan Poe, Patrick Brady, Richard Lorite, Stephan Osmont, Stephen Hyun, Steve Goldsmith, Suresh Shamanna, Thorsten Deska, Toma Bedolla, Tuan Tran <BR><BR>If we missed your name, we apologize! This list is still growing. Please let us know and tell us why at <A href=\"mailto:staff@liferay.com\">staff@liferay.com</A>.</DIV>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'COMPANY-COMMUNITY', 'COMPANY-COMMUNITY', now(), 'true', 'liferay.com.1', 'John Wayne');
insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('COMPANY-CONTACT-US', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Company Contact Us', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV><B>Email</B> \n<TABLE cellSpacing=5 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha>Sales Inquiries: <A href=\"mailto:sales@liferay.com\">sales@liferay.com</A></TD></TR>\n<TR>\n<TD class=alpha>General Inquiries: <A href=\"mailto:staff@liferay.com\">staff@liferay.com</A></TD></TR>\n<TR>\n<TD class=alpha>Support: <A href=\"mailto:support@liferay.com\">support@liferay.com</A></TD></TR></TBODY></TABLE><BR>Liferay is also supported by a large community. To join our community: \n<TABLE cellSpacing=5 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD><IMG src=\"http://@portal_url@/image/image_gallery?img_id=15\"></TD>\n<TD class=alpha>Visit <A href=\"http://forums.liferay.com/\">http://forums.liferay.com</A></TD></TR>\n<TR>\n<TD><IMG src=\"http://@portal_url@/image/image_gallery?img_id=15\"></TD>\n<TD class=alpha><A href=\"http://lists.sourceforge.net/lists/listinfo/lportal-development\">Subscribe</A> or <A href=\"http://sourceforge.net/mailarchive/forum.php?forum=lportal-development\">browse</A>&nbsp;our mailing list</TD></TR></TBODY></TABLE></DIV>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'COMPANY-CONTACT-US', 'COMPANY-CONTACT-US', now(), 'true', 'liferay.com.1', 'John Wayne');
insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('COMPANY-LEADERSHIP', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Company  Leadership', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_box\">\n    <dynamic-content><![CDATA[Liferay was formed by five partners with diverse technological and professional backgrounds. On top of a solid functional understanding of technology, our team implements innovative solutions grounded by years of experience across a broad range of industries.]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-name-1\" type=\"text_box\">\n    <dynamic-content><![CDATA[Brian Chan]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-summary-1\" type=\"text_box\">\n    <dynamic-content><![CDATA[Brian Chan is an experienced architect and consultant. Having graduated from the University of Chicago majoring in both Economics and Computer Science, Brian founded Liferay in 2000 to provide non-profit organizations with a free portal as a means to help these organizations collaborate on the Internet. As Chief Architect, Brian has helped lead Liferay Portal to become the n-tier, enterprise open source application it is today. His past experience includes work at Bank One, Public Storage, and eScore.]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-name-2\" type=\"text_box\">\n    <dynamic-content><![CDATA[Bryan Cheung]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-summary-2\" type=\"text_box\">\n    <dynamic-content><![CDATA[Bryan Cheung comes to Liferay with over five years of consulting experience. After finishing his Computer Science degree at the University of California, Berkeley, Bryan started his career at Accenture Consulting. Bryan has extensive experience in portals software, including the advertising campaign management portal which was one of the first commercial uses of Liferay. Bryan has also worked on custom Epicentric portlet development for Genentech, and has done general consulting for Fortune 500 firms such as Universal Music Group, Sony Consumer Electronics, and Gateway Computers.]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-name-3\" type=\"text_box\">\n    <dynamic-content><![CDATA[Brian Kim]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-summary-3\" type=\"text_box\">\n    <dynamic-content><![CDATA[Brian Kim is an experienced technology consultant. As a student at the University of Chicago, Brian worked alongside Brian Chan on many undergraduate projects. At HubbardOne, Brian Kim worked alongside upper management, including the CEO and CFO, to lead several core internal projects which optimized the company\'s infrastructure. After graduating with a M.S. in Computer Science at the University of Chicago, Brian joined Accenture, where he served as a technical team lead on projects for Fortune 500 firms, including Bank of America and Wachovia Securities.]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-name-4\" type=\"text_box\">\n    <dynamic-content><![CDATA[Michael Weisser]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-summary-4\" type=\"text_box\">\n    <dynamic-content><![CDATA[Michael Weisser is a graduate of FH-Wiesbaden, Germany majoring in Computer Science. Since 1996, Michael has supported many Java projects in roles ranging from architect to technical project lead. As an experienced software architect, he has worked for firms such as Dresdner Kleinwort Benson, Kreutler GmbH, ASC telecom AG, Deutsche Brse AG (German Stock Exchange), DrKW London UK, DB Systems GmbH, SunGard Forbatec GmbH. Michael currently leads the development of the Liferay Toolbar.]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-name-5\" type=\"text_box\">\n    <dynamic-content><![CDATA[Michael Young]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"person-summary-5\" type=\"text_box\">\n    <dynamic-content><![CDATA[Michael Young graduated from the University of California, San Diego majoring in Computer Science. Bringing over 10 years of enterprise computing experience, Michael Young has held positions of increasing responsibility at successful companies such as Sony Electronics, Syntricity, and the United States Navy. Michael led the overhaul of Syntricity\'s dataConductor EP data management and reporting software platform, paving Syntricity\'s way to profitability. As a contractor for the US Navy, Michael developed solutions that improved collaboration between varieties of targeting and image systems.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'COMPANY-LEADERSHIP', 'COMPANY-LEADERSHIP', now(), 'true', 'liferay.com.1', 'John Wayne');
insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('COMPANY-OVERVIEW', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Company Overview', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV><EM>\"Enterprise. Open Source. For Life.\"</EM></DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>Enterprise.</B></DIV>\n<DIV>&nbsp;</DIV>\n<DIV>Our flagship product, Liferay Portal Enterprise, has been carefully architected to handle the most demanding applications, incorporating proven technologies to ensure scalability and robust performance for firms across all industries. Adherence to J2EE standards means Liferay Portal Enterprise can be deployed in a myriad of contexts without tying you down to any specific operating system, application server, or database. From a services perspective, enterprise readiness is evident in our professional services team, whose extensive consulting experience among Fortune 500 companies coupled with expertise with Liferay Portal Enterprise has led to successful, value-driven implementations for clients all over the world.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>Open Source.</B></DIV>\n<DIV>&nbsp;</DIV>\n<DIV>By adopting open source trends, Liferay is committing to innovation, robustness, and community. Liferay is a safe technology investment for our clients because being open source leads to wider adoption by the technology community, the development of an expert user base, and acceptance as a standard portal platform. Our MIT license is business friendly and allows you to use, modify, or distribute Liferay with no license fees.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV>An open source model also encourages robustness, as real users in real deployment scenarios report enhancements and upgrades directly back to us. Finally, our commitment to open source reflects our philosophy of helping improve the world community, and the wide use of Liferay as a free portal solution by various not-for-profit organizations is a testament to our success in that endeavor.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>For Life.</B></DIV>\n<DIV>&nbsp;</DIV>\n<DIV>More importantly, Liferay is committed to making a difference in this world, and does so by financially supporting various not-for-profit organizations through a donation of a percentage of our profits every month. In the past we have made financial contributions toward causes such as AIDS relief and the Sudan refugee crisis, through well-respected organizations such as Samaritan\'s Purse and World Vision. This desire to make an impact among those in need is the heart of our company, and ultimately the reason why we exist.</DIV>\n<DIV>&nbsp;</DIV>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'COMPANY-OVERVIEW', 'COMPANY-OVERVIEW', now(), 'true', 'liferay.com.1', 'John Wayne');
insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('DOCUMENTATION-ARCHITECTURE', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Documentation Architecture', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-1\" type=\"text\">\n    <dynamic-content><![CDATA[Overview]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-1\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV style=\"FONT-WEIGHT: normal\">Users can access the portal from traditional and wireless devices. Developers can access the portal from the exposed APIs via SOAP, RMI, and our custom tunneling classes.<BR><BR><IMG src=\"http://@portal_url@/image/journal/article?img_id=DOCUMENTATION-ARCHITECTURE.architecture&amp;version=@reserved_article_version@\"></DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-2\" type=\"text\">\n    <dynamic-content><![CDATA[Portlet API (JSR 168)]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-2\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV style=\"FONT-WEIGHT: normal\">Liferay is designed to deploy portlets that adhere to the Portlet API (JSR 168). Many useful portlets are bundled with the portal (Mail, Document Library, Calendar, Message Boards, to name a few) and can be used as <A href=\"http://@cms_url@/DOCUMENTATION-DEVELOPMENT-PORTLET\">examples</A> for adding your own <A href=\"http://@cms_url@/DOCUMENTATION-FAQ#3\">custom</A> portlets.</DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-3\" type=\"text\">\n    <dynamic-content><![CDATA[Struts and Tiles]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-3\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV style=\"FONT-WEIGHT: normal\">All HTTP and WAP requests go through <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portal/servlet/MainServlet.html\">MainServlet</A> which extends the basic Struts <A href=\"http://jakarta.apache.org/struts/api/org/apache/struts/action/ActionServlet.html\">ActionServlet</A>. MainServlet processes all requests and ensures that each request is routed to the proper <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portal/struts/PortletAction.html\">PortletAction</A>. Refer to <A href=\"http://jakarta.apache.org/struts\">Struts</A> for a better understanding of how the portal\'s web framework functions.<BR><BR>All layout information for the portal is centralized in two files: portal_layout.jsp and portlet_layout.jsp. They can be found in /portal-web/docroot/WEB-INF/html/tiles. Refer to articles about <A href=\"http://www.javaworld.com/javaworld/jw-01-2002/jw-0104-tilestrut.html\">Tiles</A> to get a better understanding of how Tiles works as a layout manager.<BR><BR><A href=\"http://www.lglifeisgood.com/\">LG</A> is a good example of a site that has modified the portal\'s layout to give it a unique look and feel.</DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-4\" type=\"text\">\n    <dynamic-content><![CDATA[Session EJBs, Spring, and Hibernate]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-4\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV style=\"FONT-WEIGHT: normal\">Liferay is no longer dependent on EJBs and can be deployed on a standalone servlet container. All business logic is concentrated inside POJO implementations that are looked up and instantiated by Spring. These implementations can be modified or enhanced via Spring\'s AOP and IOC capabilities.<BR><BR>The enterprise release of the portal wraps the POJO implementations with Session EJBs to provide heavy scaling and transaction support required by large sites. The professional release of the portal calls the POJO implementations directly to provide a light weight facade.<BR><BR>All data is persisted using Hibernate and is called through the POJO implementations. Liferay used to rely on CMP technology to achieve persistence, but switched over to Hibernate because of its raw speed and flexibility. Liferay is database agnostic and can run on a variety of popular <A href=\"http://@cms_url@/DOCUMENTATION-DEVELOPMENT-DATABASES\">databases</A>.<BR><BR>Liferay uses JAAS Web security so that when a user logs in, their principal is propogated to the Servlet and EJB tiers. <I>Remote</I> Session EJBs can take advantage of this by checking security and permissions at the EJB level so it does not have be duplicated else where. <I>Local</I> Session EJBs exposes business logic to other Session EJBs and does not specifically check for security since they cannot be called remotely. Principals are also propagated to POJO implementations that are the base classes for <I>Remote</I> Session EJBs.<BR><BR>The enterprise release uses Session EJBs which allows the deployer to separate the Web server, EJB server, and database server to achieve clustering at three levels. This is true n-tier deploying because no one is forced to cluster at any single layer and allows the most flexibility for large companies.<BR><BR>Most of our EJBs, HBMs, and Models are generated through the ant task <B>build-ejb</B> which reads the file <B>ejb.xml</B> in /portal-ejb. Each portlet that persist data has its own ejb.xml (do a search in /portal-ejb and you will get a list back). We copy this file to /portal-ejb when we want to generate the persistence classes for that portlet. This is an internal tool that is built on top of the XDoclet engine.<BR><BR>For example, upon reading ejb.xml found in the Bookmarks portlet, the following model classes are generated. Each model class reflects a table in the database. Never edit BookmarksEntryModel. Do edit BookmarksEntry to add hand massaged code. BookmarksEntry is generated once and extends BookmarksEntryModel. This allows us the ease of generated code and flexibility of hand massaged code.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/model/BookmarksEntry.html\">com.liferay.portlet.bookmarks.model.BookmarksEntry</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/model/BookmarksEntryModel.html\">com.liferay.portlet.bookmarks.model.BookmarksEntryModel</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/model/BookmarksFolder.html\">com.liferay.portlet.bookmarks.model.BookmarksFolder</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/model/BookmarksFolderModel.html\">com.liferay.portlet.bookmarks.model.BookmarksFolderModel</A><BR><BR>Hibernate classes are generated that map to the model classes. This allows for an n-tier architecture for cases where your model classes are marshalled across the wire and your Hibernate classes are not.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryHBM.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryHBM</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderHBM.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderHBM</A><BR><BR>Persistence methods to add, update, delete, find, remove, and count the Hibernate entries are generated as the default persistence mechaninsm.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryPersistence.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryPersistence</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderPersistence.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderPersistence</A><BR><BR>Helper classes are generated that call the persistence methods. By default, the helper classes call the Hibernate persistence methods to update the database. You can override this in <A href=\"http://@cms_url@/DOCUMENTATION-DEVELOPMENT-PROPERTIES\">portal.properties</A> and set your own persistence class as long as it extends the default persistence class. This means you can customize where you store your data. It can be a traditional database, a LDAP server, or even something else.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryUtil.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryUtil</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderUtil.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderUtil</A><BR><BR>Pooling classes are also created to minimize object creation. Behavior can be modified in <A href=\"http://@cms_url@/DOCUMENTATION-DEVELOPMENT-PROPERTIES\">portal.properties</A>.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryPool.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryPool</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderPool.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderPool</A><BR><BR>POJO implementations that extend <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portal/ejb/PrincipalBean.html\">PrincipalBean</A> are generated to hold business logic that check the caller principal and can be called <B>remotely</B>. Calling getUserId() returns the user id of the current user. Calling getUser() returns the <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portal/model/User.html\">User</A> model that represents the current user. The Session EJB that extends the POJO implementation implements <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portal/ejb/PrincipalSessionBean.html\">PrincipalSessionBean</A>.<BR><BR>For example, these classes allow you to delete a bookmark entry or folder if and only if you are the creator of that entry or folder.<BR><BR>These classes are only generated once if they do not already exist.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManagerImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManagerImpl</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManagerImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManagerImpl</A><BR><BR>Helper classes are generated based on the POJO implementations. They help save developer time and prevent polluted code. Instead of writing many lines of code just to look up the appropriate Session EJB wrapper or POJO implementation, you simply call BookmarksEntryManagerUtil.addEntry to call the equivalent method in BookmarksEntryManagerImpl.addEntry.<BR><BR>BookmarksEntryManagerUtil calls BookmarksFolderManagerFactory to look up the class that implements BookmarksEntryManager. BookmarksFolderManagerFactory defers to Spring and settings in <A href=\"http://@cms_url@/DOCUMENTATION-DEVELOPMENT-PROPERTIES\">portal.properties</A> on whether to load the Session EJB wrapper or the plain POJO implementation. The Session EJB extends the POJO implementation.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManager.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManager</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManagerEJB.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManagerEJB</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManagerEJBImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManagerEJBImpl</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManagerFactory.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManagerFactory</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManagerHome.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManagerHome</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManagerUtil.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManagerUtil</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManager.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManager</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManagerEJB.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManagerEJB</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManagerEJBImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManagerEJBImpl</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManagerFactory.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManagerFactory</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManagerHome.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManagerHome</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManagerUtil.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManagerUtil</A><BR><BR>Tunneling classes are generated so that developers can call the POJO implementations over port 80. An example of this given in the section V of this document.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManagerHttp.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManagerHttp</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManagerHttp.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManagerHttp</A><BR><BR>Soap classes are generated so that developers can call the POJO implementations over port 80. Soap is slower than tunneling because tunneling streams requests in binary format. Soap is more flexible than tunneling because the client classes are not limited to Java.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryManagerSoap.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryManagerSoap</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderManagerSoap.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderManagerSoap</A><BR><BR>POJO implementations classes that do <B>not</B> extend <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portal/ejb/PrincipalBean.html\">PrincipalBean</A> are generated to hold business logic that do <B>not</B> check the caller principal and can be called <B>locally</B>. These classes exist so that business logic can be easily integrated with other projects.<BR><BR>These classes are only generated once if they do not already exist.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryLocalManagerImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryLocalManagerImpl</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderLocalManagerImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderLocalManagerImpl</A><BR><BR>Helper classes are also generated.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryLocalManager.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryLocalManager</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryLocalManagerEJB.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryLocalManagerEJB</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryLocalManagerEJBImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryLocalManagerEJBImpl</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryLocalManagerFactory.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryLocalManagerFactory</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryLocalManagerHome.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryLocalManagerHome</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryLocalManagerUtil.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryLocalManagerUtil</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderLocalManager.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderLocalManager</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderLocalManagerEJB.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderLocalManagerEJB</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderLocalManagerEJBImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderLocalManagerEJBImpl</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderLocalManagerFactory.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderLocalManagerFactory</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderLocalManagerHome.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderLocalManagerHome</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderLocalManagerUtil.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderLocalManagerUtil</A><BR><BR>Some of our users needed to call the Local Manager classes remotely, so Remote Manager classes that parallel their Local counterparts are also generated.<BR><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryRemoteManager.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryRemoteManager</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryRemoteManagerEJB.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryRemoteManagerEJB</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryRemoteManagerEJBImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryRemoteManagerEJBImpl</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryRemoteManagerFactory.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryRemoteManagerFactory</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryRemoteManagerHome.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryRemoteManagerHome</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksEntryRemoteManagerUtil.html\">com.liferay.portlet.bookmarks.ejb.BookmarksEntryRemoteManagerUtil</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderRemoteManager.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderRemoteManager</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderRemoteManagerEJB.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderRemoteManagerEJB</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderRemoteManagerEJBImpl.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderRemoteManagerEJBImpl</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderRemoteManagerFactory.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderRemoteManagerFactory</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderRemoteManagerHome.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderRemoteManagerHome</A><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/bookmarks/ejb/BookmarksFolderRemoteManagerUtil.html\">com.liferay.portlet.bookmarks.ejb.BookmarksFolderRemoteManagerUtil</A><BR><BR>A lot of people stay away from Session EJBs because they are heavy and require a lot of coding. Our build scripts show that you can leverage the advantages of Session EJBs while minimizing repetitive labor so that you can strike a good balance between effort and results.<BR><BR>Spring gives Liferay additional flexibility. Developers can test their POJO implementations with Liferay Portal Professional in a servlet container and deploy to production with Liferay Portal Enterprise in an application server.</DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-5\" type=\"text\">\n    <dynamic-content><![CDATA[SOAP, RMI, and Tunneling]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-5\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV style=\"FONT-WEIGHT: normal\">All of our remote POJO implementations are exposed to the external world via SOAP, RMI, and our custom tunneling classes.<BR><BR>We do not do this simply because Web services is a buzz word, but because we find it extremely useful for integration. The following is an example of a company that leveraged these resources.<BR><BR><A href=\"http://www.3sixteen.com/\">3sixteen</A> is a t-shirt company that needed to get up and running fast. They saw Liferay and wanted to use the integrated Shopping portlet. However, they thought that Liferay was too ugly for a t-shirt company that prided itself on being at the cutting edge of fashion. To solve this problem, they decided to separate their web presence into two sites: a brochure site and shopping site.<BR><BR><A href=\"http://www.3sixteen.com/\">www.3sixteen.com</A> became the pretty site built in Flash and <A href=\"http://my.3sixteen.com/c/extranet/home\">my.3sixteen.com</A> became the shopping site using the vanilla Liferay distribution. These two sites are hosted on two different Linux machines and for all intents and purposes could have resided on different continents.<BR><BR>They also needed to build a mailing list to collect email addresses for all their interested customers. To accomplish this, they added a JSP pop up box on their Flash site that would tell the portal server to add the email address to a contact in the Address Book portlet.<BR><BR>The following is a JSP snippet that shows how the guys at 3sixteen leveraged <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/addressbook/ejb/ABContactManagerHttp.html\">ABContactManagerHttp</A> to add a contact. <BR><BR>\n<DIV class=code-fragment>String URL = \"http://my.3sixteen.com\"; <BR><BR>HttpPrincipal httpPrincipal = new HttpPrincipal(URL, \"joe_bloggs\", \"password\");<BR><BR>ABContactManagerHttp.addContact(httpPrincipal, firstName, lastName, emailAddress);</DIV><BR><A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/addressbook/ejb/ABContactManagerHttp.html\">ABContactManagerHttp</A> invoked <I>addContact</I> in <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/addressbook/ejb/ABContactManagerUtil.html\">ABContactManagerUtil</A>. The invokation was sent over port 80 and received by http://my.3sixteen.com/tunnel/servlet/AuthTunnelServlet. The application server made sure the authentication matched and then processed ABContactManagerUtil as if the user with the id <B>joe_bloggs</B> was calling <I>addContact</I>. ABContactManagerUtil then called <A href=\"http://www.liferay.com/documentation/api/portal-ejb/com/liferay/portlet/addressbook/ejb/ABContactManagerImpl.html\">ABContactManagerImpl</A> to do the actual work. You can trace the logic of this by viewing the source included in the generated JavaDocs.<BR><BR>Now Joe Bloggs can log into the portal and look in his Address Book portlet to see that he has a new contact. All of the included portlets have this capability because these helper classses are generated. This means you can write applets or any other Java application to access the Session EJBs that contain your business logic. This can be a security hazard if someone had your password, so you can configure to limit the Tunnel Servlet to only listen on certain ports by editing <A href=\"http://@cms_url@/DOCUMENTATION-DEVELOPMENT-PROPERTIES\">portal.properties</A>.<BR><BR>You can also access the Session EJBs over SOAP and RMI. We will post more examples of that shortly.</DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-6\" type=\"text\">\n    <dynamic-content><![CDATA[Application Service Provider]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-6\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV style=\"FONT-WEIGHT: normal\">Liferay was built from the ground up to be used by application service providers. The following is a sample list of portals running off of one portal instance hitting one database and shows the capabilities of Liferay.<BR><BR><A href=\"http://demo.liferay.net/\">http://demo.liferay.net</A><BR><A href=\"http://my.ccuc.net/\">http://my.ccuc.net</A><BR><A href=\"http://my.3sixteen.com/\">http://my.3sixteen.com</A><BR><A href=\"http://portal.liferay.com/\">http://portal.liferay.com</A><BR><A href=\"http://www.gatewayfriends.org/\">http://www.gatewayfriends.org</A><BR><A href=\"http://www.jasonandiris.com/\">http://www.jasonandiris.com</A><BR><BR>Users in each of these portals have no information about the other portals. They are separated by domain and each portal exists in its own space based on the company\'s id.<BR><BR>See <A href=\"http://@cms_url@/DOCUMENTATION-DEVELOPMENT-ASP\">Multiple Portals</A> for detailed informaton on how to set up multiple instances of Liferay on one machine.</DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"architecture\" type=\"image\">\n    <dynamic-content>/image/journal/article?img_id=DOCUMENTATION-ARCHITECTURE.architecture&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'DOCUMENTATION-ARCHITECTURE', 'DOCUMENTATION-ARCHITECTURE', now(), 'true', 'liferay.com.1', 'John Wayne');
insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('DOCUMENTATION-ARCHITECTURE-ROAD-MAP', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Documentation Architecture Road Map', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV>The following is a list of features we plan to implement in future releases of Liferay. If there is a feature you would like to have that is not on this list, log on to <A href=\"http://support.liferay.com/\">http://support.liferay.com</A> and submit a feature request.</DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-1\" type=\"text\">\n    <dynamic-content><![CDATA[Edwards]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-1\" type=\"text_area\">\n    <dynamic-content><![CDATA[<OL>\n<LI>Q1 2005<BR><BR></LI>\n<LI>Content Management System<BR><BR></LI>\n<UL>\n<LI>Provide dynamic web authoring capability<BR><BR></LI>\n<LI>Templating system via XML Schema and XSLT<BR><BR></LI>\n<LI>Web publishing framework<BR><BR></LI></UL>\n<LI>Monitoring Tools<BR><BR></LI>\n<UL>\n<LI>Dynamic server logs<BR><BR></LI>\n<LI>Shutdown notification<BR><BR></LI>\n<LI>Terminate user session<BR><BR></LI></UL>\n<LI>Performance<BR><BR></LI>\n<UL>\n<LI>Clustered cache<BR></LI></UL></OL>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-2\" type=\"text\">\n    <dynamic-content><![CDATA[Simeon]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-2\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV>\n<OL>\n<LI>Q2 2005<BR><BR></LI>\n<LI>JSF<BR><BR></LI>\n<UL>\n<LI>Integrate with Java Server Faces and provide a JSF portlet<BR><BR></LI></UL>\n<LI>Security<BR><BR></LI>\n<UL>\n<LI>Fine grained permissions<BR><BR></LI>\n<LI>Group (community) level roles and permissions<BR><BR></LI>\n<LI>Allow users to belong to more than one company<BR><BR></LI></UL>\n<LI>Content Management System<BR><BR></LI>\n<UL>\n<LI>Provide access to the CMS through JSR 170 (Java Content Repository)</LI></UL></OL></DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-3\" type=\"text\">\n    <dynamic-content><![CDATA[Brainerd]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-3\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV>\n<OL>\n<LI>Q3 2005<BR><BR></LI>\n<LI>Multiple Portlet Instances<BR><BR></LI>\n<UL>\n<LI>Enable multiple portlet instances per page<BR><BR></LI>\n<LI>Provide GUI for easy administration<BR><BR></LI></UL>\n<LI>Layout/Navigation Enhancements<BR><BR></LI>\n<UL>\n<LI>Allow for more flexible layout configuration<BR><BR></LI></UL>\n<LI>WSRP<BR><BR></LI>\n<UL>\n<LI>Basic authentication </LI></UL></OL></DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-title-4\" type=\"text\">\n    <dynamic-content><![CDATA[Lloyd-Jones]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"section-content-4\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV>\n<OL>\n<LI>Q4 2005<BR><BR></LI>\n<LI>Workflow<BR><BR></LI>\n<UL>\n<LI>Integrate with a workflow engine (Agila/jBPM)<BR><BR></LI></UL>\n<LI>Transactions<BR><BR></LI>\n<UL>\n<LI>Implement Spring AOP transactions for Liferay Portal Professional <BR><BR></LI></UL>\n<LI>Remoting<BR><BR></LI>\n<UL>\n<LI>Expose a Java remoting interface to service layer for Liferay Portal Professional</LI></UL></OL></DIV>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'DOCUMENTATION-ARCHITECTURE-ROAD-MAP', 'DOCUMENTATION-ARCHITECTURE-ROAD-MAP', now(), 'true', 'liferay.com.1', 'John Wayne');
insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('DOCUMENTATION-DEPLOYMENT-MATRIX', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Documentation Deployment Matrix', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV>Liferay Portal wants to make it a reality the <B><I>J2EE</I></B> promise of write once run anywhere so you can reuse your existing resources and deploy the portal the way you want to when you want to. Reuse what you have or use what\'s free so you can stick with your budget and get an immediate return on investment that everyone can be happy with.<BR><BR>There are <B>650</B> ways you can deploy the portal, with more ways to come as we add support for Resin. Is there another application server or database you use that is not in this list? Contact us at <A href=\"mailto:staff@liferay.com\">staff@liferay.com</A> and we\'ll add support for it as soon as possible.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha vAlign=top>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha>\n<FIELDSET><LEGEND><B>Application Servers</B></LEGEND>\n<TABLE cellSpacing=0 cellPadding=8 border=0>\n<TBODY>\n<TR>\n<TD class=alpha>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha><B>BAS</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Borland ES @bes_version@</TD></TR>\n<TR>\n<TD class=alpha><B>JB</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>JBoss @jboss_version@</TD></TR>\n<TR>\n<TD class=alpha><B>JON</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>JOnAS @jonas_version@</TD></TR>\n<TR>\n<TD class=alpha><B>JR</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>JRun @jrun_version@</TD></TR>\n<TR>\n<TD class=alpha><B>O</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Oracle9iAS @oracle_version@</TD></TR>\n<TR>\n<TD class=alpha><B>ORI</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Orion @orion_version@</TD></TR>\n<TR>\n<TD class=alpha><B>P</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Pramati @pramati_version@</TD></TR>\n<TR>\n<TD class=alpha><B>REX</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>RexIP @rexip_version@</TD></TR>\n<TR>\n<TD class=alpha><B>SUN</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Sun JSAS @sun_version@</TD></TR>\n<TR>\n<TD class=alpha><B>WL</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>WebLogic @weblogic_version@</TD></TR>\n<TR>\n<TD class=alpha><B>WS</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>WebSphere @websphere_version@</TD></TR></TBODY></TABLE></TD></TR></TBODY></TABLE></FIELDSET></TD></TR></TBODY></TABLE></TD>\n<TD class=alpha width=10>&nbsp;</TD>\n<TD class=alpha vAlign=top>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha>\n<FIELDSET><LEGEND><B>Servlet Containers</B></LEGEND>\n<TABLE cellSpacing=0 cellPadding=8 border=0>\n<TBODY>\n<TR>\n<TD class=alpha>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha><B>JET</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Jetty 5.1.0</TD></TR>\n<TR>\n<TD class=alpha><B>RES</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Resin</TD></TR>\n<TR>\n<TD class=alpha><B>TOM</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Tomcat 5.0.28</TD></TR></TBODY></TABLE></TD></TR></TBODY></TABLE></FIELDSET></TD></TR>\n<TR>\n<TD><IMG height=4 hspace=0 src=\"http://@portal_url@/image/image_gallery?img_id=14\" width=1 border=0></TD></TR>\n<TR>\n<TD class=alpha>\n<FIELDSET><LEGEND><B>OS</B></LEGEND>\n<TABLE cellSpacing=0 cellPadding=8 border=0>\n<TBODY>\n<TR>\n<TD class=alpha>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha><B>BSD</B></TD></TR>\n<TR>\n<TD class=alpha><B>Linux</B></TD></TR>\n<TR>\n<TD class=alpha><B>Solaris</B></TD></TR>\n<TR>\n<TD class=alpha><B>Windows</B></TD></TR>\n<TR>\n<TD class=alpha><B>Mac OS X</B></TD></TR></TBODY></TABLE></TD></TR></TBODY></TABLE></FIELDSET></TD></TR></TBODY></TABLE></TD>\n<TD class=alpha width=10>&nbsp;</TD>\n<TD class=alpha vAlign=top>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha>\n<FIELDSET><LEGEND><B>Databases</B></LEGEND>\n<TABLE cellSpacing=0 cellPadding=8 border=0>\n<TBODY>\n<TR>\n<TD class=alpha>\n<TABLE cellSpacing=0 cellPadding=0 border=0>\n<TBODY>\n<TR>\n<TD class=alpha><B>DB2</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>DB2</TD></TR>\n<TR>\n<TD class=alpha><B>FIRE</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Firebird</TD></TR>\n<TR>\n<TD class=alpha><B>HYP</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Hypersonic</TD></TR>\n<TR>\n<TD class=alpha><B>INT</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>InterBase</TD></TR>\n<TR>\n<TD class=alpha><B>JDS</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>JDataStore</TD></TR>\n<TR>\n<TD class=alpha><B>MY</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>MySQL</TD></TR>\n<TR>\n<TD class=alpha><B>O</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>Oracle</TD></TR>\n<TR>\n<TD class=alpha><B>PSQL</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>PostgresSQL</TD></TR>\n<TR>\n<TD class=alpha><B>SAP</B></TD>\n<TD class=alpha>&nbsp;&nbsp;&nbsp;</TD>\n<TD class=alpha>SAP</TD></TR></TBODY></TABLE></TD></TR></TBODY></TABLE></FIELDSET></TD></TR></TBODY></TABLE></TD></TR></TBODY></TABLE></DIV>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'DOCUMENTATION-DEPLOYMENT-MATRIX', 'DOCUMENTATION-DEPLOYMENT-MATRIX', now(), 'true', 'liferay.com.1', 'John Wayne');
