insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('NEWS-INDEX', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'News Index', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV><B>1.31.2005</B><BR>Liferay Portal 3.2.0 is released.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>12.14.2004</B><BR>Liferay Portal 3.1.0 is 100% WSRP <A href=\"http://www.oasis-open.org/committees/tc_home.php?wg_abbrev=wsrp\">compliant</A>.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>12.6.2004</B><BR>Liferay helps <A href=\"http://@cms_url@/COMPANY-CASE-STUDIES-OAKWOOD\">Oakwood</A> launch its new website built on top of Liferay Portal Enterprise.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>11.22.2004</B><BR>Sun interviews <A href=\"https://www.dev.java.net/files/documents/1654/9082/bc.html\">Brian Chan</A> on Liferay Portal 3.0.0.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>10.10.2004</B><BR>Liferay leads in <A href=\"http://blogs.ittoolbox.com/eai/leadership/archives/001792.asp\">portal market</A> among large enterprises.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>9.9.2004</B><BR>Sean Goggins presents Liferay to the <A href=\"http://www.ociweb.com/javasig/knowledgebase/2004-09/\">St. Louis Java Users Group</A>.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>3.31.2004</B><BR>Liferay Portal Enterprise 2.1.0 is 100% JSR 168 <A href=\"http://@portal_url@/image/journal/article?img_id=NEWS-INDEX.portlet-tck&amp;version=1.0\">compliant</A>.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>7.22.2003</B><BR><A href=\"http://www.oetrends.com/cgi-bin/page_display.cgi?238\">Open Enterprise Trends</A> gives tips from Liferay and other open source projects.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>4.3.2003</B><BR><A href=\"http://javaboutique.internet.com/reviews/Enterprise_Portals/\">Java Boutique</A> reviews Liferay and other open source portal solutions.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>3.12.2003</B><BR>Liferay is showcased at <A href=\"http://www.cebit.de/\">CeBIT</A>.</DIV>\n<DIV>&nbsp;</DIV>\n<DIV><B>2.18.2003</B><BR><A href=\"http://www.forrester.com/Research/LegacyIT/Excerpt/0,7208,29419,00.html\">Forrester</A> recommends Liferay to reduce costs of portal implementations.</DIV>]]></dynamic-content>\n  </dynamic-element>\n  <dynamic-element name=\"portlet-tck\" type=\"image\">\n    <dynamic-content>/image/journal/article?img_id=NEWS-INDEX.portlet-tck&amp;version=1.0</dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'NEWS-INDEX', 'NEWS-INDEX', now(), 'true', 'liferay.com.1', 'John Wayne');
insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('PRODUCTS-PORTAL', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Products Portal', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[Liferay Portal is an open soure portal that helps organizations collaborate more efficiently by providing a consolidated view of disparate applications. It is used by large and small organizations all over the world. Liferay has an extensive list of features that compares with most commercial portals, but without the high license fees. A live demo is available at <A href=\"http://demo.liferay.net/\">http://demo.liferay.net</A>.<BR><BR><B>Open Source License</B><BR><BR>Liferay is made available under the <A href=\"http://www.opensource.org/licenses/mit-license.php\">MIT license</A>, which is essentially the same as the Apache and BSD licenses. The MIT license is business friendly and contains no license fees or reciprocity clauses. You can bundle Liferay or your branch of Liferay and even resell it if you want. We do recommend that companies contribute their bug fixes and modifications back to Liferay so the community at large can benefit. It also allows your organization to easily upgrade to the latest version of Liferay.<BR><BR><B>Features</B><BR><BR>\n<UL>\n<LI><B>JSR 168 (Portlet API) Compliant</B><BR><BR>You can hot deploy any JSR 168 compliant portlet. This means you can provide more functionality to your end users by writing custom portlets. Or, you can deploy portlets that you buy from a portlet vendor. </LI><BR><BR>\n<LI><B>WSRP Compliant</B><BR><BR>WSRP is a web services standard that allows you to <I>publish</I> portlets from your portal to external portals and allows you to <I>consume</I> portlets in your portal from external portals. This allows you to buy and sell portlet functionality as a service. </LI><BR><BR>\n<LI><B>CMS</B><BR><BR>Liferay provides content management system functionality via a set of portlets that give your organization a flexible templating tool built on top of XSLT technologies. This allows you to build your site while cleanly separating content from the look and feel of a site. This page is served by the CMS tool. We will be JSR 170 (CMS API) compliant after the specification is finalized. </LI><BR><BR>\n<LI><B>SSO</B><BR><BR>Liferay provides a built in connector for <A href=\"http://tp.its.yale.edu/tiki/tiki-index.php?page=CentralAuthenticationService\">CAS</A>, Yale\'s single sign on engine. You can write custom hooks to integrate with other SSO engines like <A href=\"http://www.netegrity.com/\">Netegrity</A>.<BR><BR>Liferay can also synchronize its user list between the portal and an external data source like another database or LDAP server. A default connector for Microsoft Exchange is bundled with the portal. </LI><BR><BR>\n<LI><B>ASP Model</B><BR><BR>Liferay was designed from the ground up to be used by application service providers. This means you can host multiple instances of the portal (distinguished by unique URLs) on one application server and database. </LI><BR><BR>\n<LI><B>Application Server Agnostic</B><BR><BR>Unlike portals that come from application server vendors, Liferay is designed to be application server agnostic so you are not locked into a specific server. Liferay will work on lightweight servlet containers like Jetty and Tomcat, or on J2EE compliant servers like Borland ES, JBoss+Jetty/Tomcat, JOnAS+Jetty/Tomcat, JRun, Oracle9iAS, Orion, Pramati, RexIP, Sun JSAS, WebLogic, and WebSphere.<BR><BR>An added bonus of being a Java portal means Liferay will work on many operating systems: BSD (FreeBSD, NetBSD, OpenBSD), Linux (Fedora, Novell), Solaris, Mac OS X, and Windows. </LI><BR><BR>\n<LI><B>Spring, EJB, and AOP</B><BR><BR>Liferay\'s business beans are built on top of Spring. This allows you to leverage Spring\'s AOP, IOC, and proxy features to customize Liferay.<BR><BR>We use Spring to decide whether to call the POJO implementation of a business bean or the EJB wrapped implementation of a business bean. This allows deployers to decide whether to deploy Liferay on a heavyweight application server like Borland ES (and thus leverage VisiBroker\'s transaction features) or a lightweight container like Tomcat. </LI><BR><BR>\n<LI><B>Database Agnostic</B><BR><BR>Liferay uses <A href=\"http://www.hibernate.org/\">Hibernate</A> as the ORM tool for the persistence layer which enables pluggable databases (DB2, Firebird, Hypersonic, InterBase, JDataStore, MySQL, Oracle, PostgreSQL, SAP, SQL Server). This allows your organization to leverage existing resources without having to purchase new database hardware and software. </LI><BR><BR>\n<LI><B>Scalable N-Tier Cluster</B><BR><BR>Liferay is very scalable and uses <A href=\"http://www.opensymphony.com/oscache\">OSCache</A> to provide deployers with a clustered cache. You can scale by adding more nodes without sacrificing on caching.<BR><BR>You can cluster the enterprise release of Liferay in multiple tiers: presentation tier, business logic tier, and database tier to meet your specific load requirements. </LI><BR><BR>\n<LI><B>Struts and Tiles</B><BR><BR>Liferay leverages off of <A href=\"http://struts.apache.org/\">Struts</A> to follow the MVC pattern. Most programmers are already familiar with Struts, which means your developers will have an easier time of writing portlets in a familiar framework.<BR><BR>The look and feel of the portal can be easily customized and reskinned because the display logic is concentrated in a few template files read by <A href=\"http://struts.apache.org/userGuide/dev_tiles.html\">Tiles</A>. </LI><BR><BR>\n<LI><B>Internationalization</B><BR><BR>Liferay can display and receive input in multiple languages. Language resources for Chinese, Dutch, English, French, German, Greek, Italian, Japanese, Korean, Portuguese, Spanish, Turkish, and Vietnamese are already included. </LI><BR><BR>\n<LI><B>Personalization</B><BR><BR>Portlets can be rearranged to the unique preferences of a user or community. Move things up, down, and all around. </LI><BR><BR>\n<LI><B>Administration</B><BR><BR>Liferay allows administrators to easily manage <B><I>users, groups, and roles</I></B> through a GUI interface. Groups signify a collection of users. Roles signify permissions that a group or user can be bound to. Access to portlets are also restricted to users based on roles.<BR><BR>Administrators can also specify <B><I>community pages</I></B> so that all users who belong to a certain group see the same page. </LI><BR><BR>\n<LI><B>Out of the Box Portlets</B><BR><BR>Liferay provides many useful portlets: Blogs, Calendar, Document Library, Journal (CMS), Image Gallery, Mail, Message Boards, Polls, RSS, and Wiki.<BR><BR>Portals are only useful so long as there are portlets that provide functionality. Our bundled portlets are a great starting point for a portal deployment. They also serve as a large code base of examples from which you can glean patterns on how to write portlets. </LI><BR><BR>\n<LI><B>Community Based Portlets</B><BR><BR>Many of the bundled portlets are community aware. For example, if you add an event to your <I>HR</I> community calendar, then <I>Marketing</I> would not be aware of it. </LI><BR><BR></UL><B>Liferay vs. Closed Source Portals</B><BR><BR>Liferay provides the lowest total cost of ownership. There are ZERO license fees. Don\'t pay more because you add more users or need to scale with more CPUs and servers.<BR><BR>Liferay has no application server agenda. Don\'t use a portal simply because it was made by the same vendor that made your application server so they can sell you more licenses. Mix and match so you get the best of breed.<BR><BR>You can develop portlets with a full featured IDE like Eclipse, Netbeans, and JBuilder or with lightweight editor like EditPlus, VI, and Emacs. Commercial portals love to sell tools that <A href=\"http://www.theserverside.com/news/thread.tss?thread_id=31302#153713\">promise</A> to save you time and money but almost always end up wasting you time and money. This isn\'t 1999. Save money.<BR><BR>Open source means the code is in your hands. If you want to customize it, you <I>can</I>. If there are security flaws, everyone is made aware and the community can partipate in fixing it quickly. There is actually the <I>less</I> risk in going with a sustained open source product than in going with a commercial vendor. Vendors can go out of business. Vendors can merge and render your product dead in the water. Sustained open source products have an active community around it that works together.<BR><BR><B>Liferay vs. Open Source Portals</B><BR><BR>Not all open source licenses are created equal. Some licenses require that your modifications be contributed if you choose to sell or distribute your product. Some open source vendors still charge a license fee for commercial use. Others are confused and flip flop from being closed source one minute and open source the next minute. Liferay is open source because we believe it is the next step in the evolution of software development.<BR><BR>Liferay has a very active community as evidenced by our active <A href=\"http://forums.liferay.com/\">forums</A>. If you\'re a small organization that doesn\'t require mission critical response times, you\'ll get the help you need. If your organization requires mission critical support so your project deadline is not compromised, then you can get direct access to the developers of Liferay through <B><I>professional support</I></B> and consulting.<BR><BR>Using Liferay means you get a good portal with no license fees. But if you need help, you\'re not at the mercy of volunteers but can get professional support. We don\'t claim to be the best portal that can meet everyone\'s needs, but we just may be the best portal to meet <I>your</I> needs. Feel free to contact us at <A href=\"mailto:staff@liferay.com\">staff@liferay.com</A>.]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'PRODUCTS-PORTAL', 'PRODUCTS-PORTAL', now(), 'true', 'liferay.com.1', 'John Wayne');
insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('PRODUCTS-TOOLBAR', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Products Toolbar', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV><B>Liferay Toolbar</B> allows you to access the portal\'s features directly from your desktop. The toolbar communicates to the portal via HTTP so it can work around firewalls. The toolbar is in <I>incubation</I> phase and is spearheaded by Michael Weisser.<BR><BR>To use the toolbar, you must have an existing account on <A href=\"http://demo.liferay.net/\">http://demo.liferay.net</A> (demo of the portal).<BR><BR>Please be patient while your browser downloads the toolbar. It will not add any spyware or adware. It\'s just an applet that shows how you can add bookmarks from the portal or from your toolbar.<BR><BR>The following are screen shots of how to use the toolbar.<BR><BR>Go to on <I>File</I> and then <I>Preferences</I>.<BR><BR><A href=\"http://@portal_url@/image/journal/article?img_id=PRODUCTS-TOOLBAR.screenshot-full-1&amp;version=@reserved_article_version@\"><IMG hspace=0 src=\"http://@portal_url@/image/journal/article?img_id=PRODUCTS-TOOLBAR.screenshot-thumbnail-1&amp;version=@reserved_article_version@\" border=1></A><BR><BR>Set the <I>portal URL</I> to <I>http://demo.liferay.net</I> and the company id to <I>liferay.net</I>.<BR><BR><A href=\"http://@portal_url@/image/journal/article?img_id=PRODUCTS-TOOLBAR.screenshot-full-2&amp;version=@reserved_article_version@\"><IMG hspace=0 src=\"http://@portal_url@/image/journal/article?img_id=PRODUCTS-TOOLBAR.screenshot-thumbnail-2&amp;version=@reserved_article_version@\" border=1></A><BR><BR>Go to on <I>File</I> and then <I>Login</I>.<BR><BR><A 