insert into JournalArticle (articleId, version, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, title, content, type_, structureId, templateId, displayDate, approved, approvedByUserId, approvedByUserName) values ('SERVICES-INDEX', 1.0, '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'Services Index', '<?xml version=\"1.0\"?>\n\n<root>\n  <dynamic-element name=\"main-text\" type=\"text_area\">\n    <dynamic-content><![CDATA[<DIV>Liferay offers a full variety of professional services to help integrate your existing systems with Liferay Portal Enterprise.<BR><BR>\n<UL>\n<LI><B>Support</B><BR><BR>Hands-on Liferay experts are available to support your portal needs. Your organization will be given multiple accounts to our <A href=\"http://support.liferay.com/\">support tracker</A> as needed. When you submit a question to the tracker, one of our Liferay experts will be notified immediately. Replies are sent within the response time for your level of support and often come much more quickly.<BR><BR>Support is available during regular business hours across the continental United States. You receive 2 months of free support when purchasing an annual agreement.<BR><BR>\n<TABLE cellSpacing=1 cellPadding=4 border=1>\n<TBODY>\n<TR>\n<TD class=alpha></TD>\n<TD class=alpha bgColor=#aaaaaa><FONT color=#ffffff>Response Method</FONT></TD>\n<TD class=alpha bgColor=#aaaaaa><FONT color=#ffffff>Response Time</FONT></TD>\n<TD class=alpha bgColor=#aaaaaa><FONT color=#ffffff>Monthly Price</FONT></TD>\n<TD class=alpha bgColor=#aaaaaa><FONT color=#ffffff>Annual Price</FONT></TD></TR>\n<TR>\n<TD class=alpha bgColor=#777777><FONT color=#ffffff>Level 3</FONT></TD>\n<TD class=alpha>Email</TD>\n<TD class=alpha>1 business day</TD>\n<TD class=alpha>$1,000</TD>\n<TD class=alpha>$10,000</TD></TR>\n<TR>\n<TD class=alpha bgColor=#777777><FONT color=#ffffff>Level 2</FONT></TD>\n<TD class=alpha>Email/Phone</TD>\n<TD class=alpha>4 hours</TD>\n<TD class=alpha>$3,000</TD>\n<TD class=alpha>$30,000</TD></TR>\n<TR>\n<TD class=alpha bgColor=#777777><FONT color=#ffffff>Level 1</FONT></TD>\n<TD class=alpha>Email/Phone</TD>\n<TD class=alpha>2 hours</TD>\n<TD class=alpha>$6,000</TD>\n<TD class=alpha>$60,000</TD></TR></TBODY></TABLE><BR></LI>\n<LI><B>Training</B><BR><BR>Training is tailored to fit your organization\'s unique needs and can last anywhere from 2 days to a few weeks. Topics include development of portlets according to the JSR 168 (Portlet API) spec, customization of Liferay, and deployment of Liferay in a production environment.<BR><BR>The cost for training 1 to 3 developers at our location in Los Angeles is $1,000 per day. Contact us for pricing on getting a Liferay expert to fly to your organization\'s location.<BR><BR></LI>\n<LI><B>Custom Development</B> <BR><BR>Liferay experts are available to help migrate your organization\'s existing applications to Liferay Portal Enterprise. We can also write custom portlets or help your developers write custom portlets. Contact us for pricing.</LI></UL>For more information, contact us at <A href=\"mailto:sales@liferay.com\">sales@liferay.com</A>. </DIV>]]></dynamic-content>\n  </dynamic-element>\n</root>', 'general', 'SERVICES-INDEX', 'SERVICES-INDEX', now(), 'true', 'liferay.com.1', 'John Wayne');

