insert into JournalTemplate (templateId, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, structureId, name, description, xsl, smallImage, smallImageURL) values ('COMMON-FUNCTIONS', '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), '', 'Common Functions', 'Common Functions', '<?xml version=\"1.0\"?>\n\n<xsl:stylesheet xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\" version=\"1.0\">\n  <xsl:variable name=\"reserved_article_id\">\n    <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'reserved-article-id\']/dynamic-content\"/>\n  </xsl:variable>\n  <xsl:variable name=\"reserved_article_version\">\n    <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'reserved-article-version\']/dynamic-content\"/>\n  </xsl:variable>\n  <xsl:variable name=\"reserved_article_title\">\n    <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'reserved-article-title\']/dynamic-content\"/>\n  </xsl:variable>\n  <xsl:variable name=\"reserved_article_create_date\">\n    <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'reserved-article-create-date\']/dynamic-content\"/>\n  </xsl:variable>\n  <xsl:variable name=\"reserved_article_modified_date\">\n    <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'reserved-article-modified-date\']/dynamic-content\"/>\n  </xsl:variable>\n  <xsl:variable name=\"reserved_article_display_date\">\n    <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'reserved-article-display-date\']/dynamic-content\"/>\n  </xsl:variable>\n  <xsl:template name=\"string-replace\">\n    <xsl:param name=\"string\"/>\n    <xsl:param name=\"old\"/>\n    <xsl:param name=\"new\"/>\n    <xsl:choose>\n      <xsl:when test=\"contains($string, $old)\">\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"substring-before($string, $old)\"/>\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$new\"/>\n        <xsl:call-template name=\"string-replace\">\n          <xsl:with-param name=\"string\" select=\"substring-after($string, $old)\"/>\n          <xsl:with-param name=\"old\" select=\"$old\"/>\n          <xsl:with-param name=\"new\" select=\"$new\"/>\n        </xsl:call-template>\n      </xsl:when>\n      <xsl:otherwise>\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$string\"/>\n      </xsl:otherwise>\n    </xsl:choose>\n  </xsl:template>\n</xsl:stylesheet>', 'false', '');
insert into JournalTemplate (templateId, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, structureId, name, description, xsl, smallImage, smallImageURL) values ('COMPANY-CASE-STUDIES', '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), 'COMPANY-CASE-STUDIES', 'Company Case Studies', 'Company Case Studies', '<?xml version=\"1.0\"?>\r\n\r\n<xsl:stylesheet xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\" version=\"1.0\">\r\n  <xsl:import href=\"http://@portal_url@/c/journal/get_template?template_id=COMPANY-SUB-CONTENT\"/>\r\n  <xsl:output method=\"html\" omit-xml-declaration=\"yes\"/>\r\n  <xsl:variable name=\"sub_content_menu_beta_id\">\r\n    <xsl:text>COMPANY-CASE-STUDIES</xsl:text>\r\n  </xsl:variable>\r\n  <xsl:template name=\"sub-content\">\r\n    <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'main-text\']/dynamic-content\"/>\r\n    <br/>\r\n    <table align=\"center\" border=\"0\" cellpadding=\"10\" cellspacing=\"0\" width=\"500\">\r\n      <xsl:call-template name=\"loop-study\">\r\n        <xsl:with-param name=\"iterator\" select=\"1\"/>\r\n        <xsl:with-param name=\"total\" select=\"10\"/>\r\n      </xsl:call-template>\r\n    </table>\r\n  </xsl:template>\r\n  <xsl:template name=\"loop-study\">\r\n    <xsl:param name=\"iterator\"/>\r\n    <xsl:param name=\"total\"/>\r\n    <xsl:if test=\"number($total) &gt;= number($iterator)\">\r\n      <xsl:variable name=\"study_name_var\">\r\n        <xsl:text>study-name-</xsl:text>\r\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$iterator\"/>\r\n      </xsl:variable>\r\n      <xsl:variable name=\"study_name_value\">\r\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=$study_name_var]/dynamic-content\"/>\r\n      </xsl:variable>\r\n      <xsl:variable name=\"study_url_var\">\r\n        <xsl:text>study-url-</xsl:text>\r\n        <xsl:value-of disable-output-escaping=\"yes\" sele