isable-output-escaping=\"yes\" select=\"$study_url_value\"/>\r\n              </xsl:attribute>\r\n              <xsl:value-of disable-output-escaping=\"yes\" select=\"$study_url_value\"/>\r\n            </a>\r\n            <br/>\r\n            <xsl:value-of disable-output-escaping=\"yes\" select=\"$study_summary_value\"/>\r\n            <xsl:if test=\"$study_details_url_value != \'\'\">\r\n              <br/>\r\n              <br/>\r\n              <img border=\"0\" src=\"http://@portal_url@/image/image_gallery?img_id=16\"/>&#160;\r\n              <a>\r\n                <xsl:attribute name=\"href\">\r\n                  <xsl:value-of disable-output-escaping=\"yes\" select=\"$study_details_url_value\"/>\r\n                </xsl:attribute>\r\n                <xsl:text>View Case Study</xsl:text>\r\n              </a>\r\n            </xsl:if>\r\n          </td>\r\n          <td>\r\n            <img>\r\n              <xsl:attribute name=\"src\">\r\n                <xsl:value-of disable-output-escaping=\"yes\" select=\"$study_logo_value\"/>\r\n              </xsl:attribute>\r\n            </img>\r\n          </td>\r\n        </tr>\r\n        <tr>\r\n          <td align=\"center\">\r\n            <img src=\"http://@portal_url@/image/image_gallery?img_id=20\"/>\r\n          </td>\r\n          <td/>\r\n        </tr>\r\n      </xsl:if>\r\n      <xsl:call-template name=\"loop-study\">\r\n        <xsl:with-param name=\"iterator\" select=\"$iterator + 1\"/>\r\n        <xsl:with-param name=\"total\" select=\"$total\"/>\r\n      </xsl:call-template>\r\n    </xsl:if>\r\n  </xsl:template>\r\n</xsl:stylesheet>', 'false', '');
