plate name=\"loop-step\">\n        <xsl:with-param name=\"iterator\" select=\"$iterator + 1\"/>\n        <xsl:with-param name=\"total\" select=\"$total\"/>\n        <xsl:with-param name=\"step_prefix\" select=\"$step_prefix\"/>\n      </xsl:call-template>\n    </xsl:if>\n  </xsl:template>\n  <xsl:template name=\"step-detail\">\n    <xsl:param name=\"step_var\"/>\n    <xsl:variable name=\"step_value\">\n      <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=$step_var]/dynamic-content\"/>\n    </xsl:variable>\n    <xsl:variable name=\"app_server_name\">\n      <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'app-server-name\']/dynamic-content\"/>\n    </xsl:variable>\n    <xsl:variable name=\"app_server_version\">\n      <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'app-server-version\']/dynamic-content\"/>\n    </xsl:variable>\n    <xsl:variable name=\"app_server_url\">\n      <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'app-server-url\']/dynamic-content\"/>\n    </xsl:variable>\n    <xsl:variable name=\"deploy_file\">\n      <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'deploy-file\']/dynamic-content\"/>\n    </xsl:variable>\n    <xsl:variable name=\"bundle_file\">\n      <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'bundle-file\']/dynamic-content\"/>\n    </xsl:variable>\n    <xsl:variable name=\"bundle_run_command\">\n      <xsl:value-of disable-output-escaping=\"yes\" select=\"root/dynamic-element[@name=\'bundle-run-command\']/dynamic-content\"/>\n    </xsl:variable>\n    <xsl:if test=\"$step_value != \'\'\">\n      <xsl:variable name=\"step_value_formatted\">\n        <xsl:call-template name=\"string-replace\">\n          <xsl:with-param name=\"string\" select=\"$step_value\"/>\n          <xsl:with-param name=\"old\">@app_server_name@</xsl:with-param>\n          <xsl:with-param name=\"new\" select=\"$app_server_name\"/>\n        </xsl:call-template>\n      </xsl:variable>\n      <xsl:variable name=\"step_value\">\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$step_value_formatted\"/>\n      </xsl:variable>\n      <xsl:variable name=\"step_value_formatted\">\n        <xsl:call-template name=\"string-replace\">\n          <xsl:with-param name=\"string\" select=\"$step_value\"/>\n          <xsl:with-param name=\"old\">@app_server_version@</xsl:with-param>\n          <xsl:with-param name=\"new\" select=\"$app_server_version\"/>\n        </xsl:call-template>\n      </xsl:variable>\n      <xsl:variable name=\"step_value\">\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$step_value_formatted\"/>\n      </xsl:variable>\n      <xsl:variable name=\"step_value_formatted\">\n        <xsl:call-template name=\"string-replace\">\n          <xsl:with-param name=\"string\" select=\"$step_value\"/>\n          <xsl:with-param name=\"old\">@app_server_url@</xsl:with-param>\n          <xsl:with-param name=\"new\" select=\"$app_server_url\"/>\n        </xsl:call-template>\n      </xsl:variable>\n      <xsl:variable name=\"step_value\">\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$step_value_formatted\"/>\n      </xsl:variable>\n      <xsl:variable name=\"step_value_formatted\">\n        <xsl:call-template name=\"string-replace\">\n          <xsl:with-param name=\"string\" select=\"$step_value\"/>\n          <xsl:with-param name=\"old\">@deploy_file@</xsl:with-param>\n          <xsl:with-param name=\"new\" select=\"$deploy_file\"/>\n        </xsl:call-template>\n      </xsl:variable>\n      <xsl:variable name=\"step_value\">\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$step_value_formatted\"/>\n      </xsl:variable>\n      <xsl:variable name=\"step_value_formatted\">\n        <xsl:call-template name=\"string-replace\">\n          <xsl:with-param name=\"string\" select=\"$step_value\"/>\n          <xsl:with-param name=\"old\">@bundle_file@</xsl:with-param>\n          <xsl:with-param name=\"new\" select=\"$bundle_file\"/>\n        </xsl:call-template>\n      </xsl:variable>\n      <xsl:variable name=\"step_value\">\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$step_value_formatted\"/>\n      </xsl:variable>\n      <xsl:variable name=\"step_value_formatted\">\n        <xsl:call-template name=\"string-replace\">\n          <xsl:with-param name=\"string\" select=\"$step_value\"/>\n          <xsl:with-param name=\"old\">@bundle_run_command@</xsl:with-param>\n          <xsl:with-param name=\"new\" select=\"$bundle_run_command\"/>\n        </xsl:call-template>\n      </xsl:variable>\n      <xsl:variable name=\"step_value\">\n        <xsl:value-of disable-output-escaping=\"yes\" select=\"$step_value_formatted\"/>\n      </xsl:variable>\n      <xsl:value-of disable-output-escaping=\"yes\" select=\"$step_value\"/>\n      <br/>\n    </xsl:if>\n  </xsl:template>\n</xsl:stylesheet>', 'false', '');
