insert into JournalTemplate (templateId, portletId, groupId, companyId, userId, userName, createDate, modifiedDate, structureId, name, description, xsl, smallImage, smallImageURL) values ('LAYOUT-HEAD-SCRIPT', '15', '3', 'liferay.com', 'liferay.com.1', 'John Wayne', now(), now(), '', 'Layout Head Script', 'Layout Head Script', '// Rollovers\r\n\r\nvar img = new Array();\r\nvar objCount = 0;\r\n\r\nfunction preload(name, on, off) {\r\n	img[objCount] = new Array(3);\r\n	img[objCount][0] = new Image();\r\n	img[objCount][0].src = on;\r\n	img[objCount][1] = new Image();\r\n	img[objCount][1].src = off;\r\n	img[objCount][2] = name;\r\n	objCount++;\r\n}\r\n\r\nfunction on(name) {\r\n	for (i = 0; i < objCount; i++) {\r\n		if (document.images[img[i][2]] != null) {\r\n		if (name != img[i][2]) {\r\n			document.images[img[i][2]].src = img[i][1].src;\r\n			}\r\n			else {\r\n				document.images[img[i][2]].src = img[i][0].src;\r\n			}\r\n		}\r\n	}\r\n}\r\n\r\nfunction off() {\r\n	for (i = 0; i < objCount; i++) {\r\n		if (document.images[img[i][2]] != null) {\r\n			document.images[img[i][2]].src = img[i][1].src;\r\n		}\r\n	}\r\n}\r\n\r\n//  Random\r\n\r\nfunction random(min, max) {\r\n	return (Math.round(Math.random() * (max - min))) + min;\r\n}', 'false', '');
